OpenPipe=function(){this.pagelets=new Array();this.head=window.document.getElementsByTagName("head")[0];this.body=window.document.body||window.document.getElementsByTagName("body")[0]};OpenPipe.prototype.onPageletArrive=function(a){this.pagelets.push(a);this._loadCSS(a.css);this._showHTML(a.id,a.html)};OpenPipe.prototype.close=function(){for(var a=0;a<this.pagelets.length;a++){this._loadJS(this.pagelets[a].js)}};OpenPipe.prototype._loadCSS=function(a){var b=window.document.createElement("style");b.setAttribute("type","text/css");if(b.textContent){b.textContent=a}else{b.innerHTML=a}this.head.appendChild(b)};OpenPipe.prototype._showHTML=function(c,b){var a=window.document.getElementById(c);a.innerHTML=b};OpenPipe.prototype._loadJS=function(b){var a=window.document.createElement("script");if(a.textContent){a.textContent=b}else{a.innerHTML=b}this.body.appendChild(a)};